mapper.update:
UPDATE get_badge
SET is_selected = 'N'
WHERE member_id = #{memberId}

UPDATE get_badge
SET is_selected = 'Y'
WHERE member_id = #{memberId} AND badge_id = #{badgeId}

mapper.select:
SELECT COUNT(*) > 0
FROM get_badge
WHERE member_id = #{memberId}
AND badge_id = #{badgeId}

mapper.update:
UPDATE get_badge
SET is_selected = 'Y'
WHERE member_id = #{memberId}
AND badge_id = (
SELECT badge_id FROM badge WHERE level = 0 LIMIT 1
)
