mapper.select:
SELECT COUNT(*)
FROM task
WHERE member_id = #{memberId}

SELECT COUNT(*)
FROM task
WHERE member_id = #{memberId}
  AND created_at BETWEEN #{startOfMonth} AND #{endOfMonth}

SELECT COUNT(*)
FROM task
WHERE member_id = #{memberId}
  AND is_checked = 'Y'
  AND created_at BETWEEN #{startOfMonth} AND #{endOfMonth}
